mapper.select:
select 

from TODO_LIST
where SYSTEM_CD = #{systemCd,jdbcType=VARCHAR}
  and PROJECT_CD = #{projectCd,jdbcType=VARCHAR}
  and TICKET_NO = #{ticketNo,jdbcType=VARCHAR}

mapper.delete:
delete from TODO_LIST
where SYSTEM_CD = #{systemCd,jdbcType=VARCHAR}
  and PROJECT_CD = #{projectCd,jdbcType=VARCHAR}
  and TICKET_NO = #{ticketNo,jdbcType=VARCHAR}
